con:config.script:
// By convention the attaments file folder  will  be located at '${projectDir}'/attachments
def projectPath = context.expand( '${projectDir}')
log.info("projectPath: "+projectPath)

// attachments folder
def attachmentsFolder= projectPath + '/' + 'attachments'
log.info("attachmentsFolder: "+attachmentsFolder)

def tProject = testRunner.getTestCase().getTestSuite().getProject()
log.info("tProject : "+tProject )

// set custom  project  property
tProject.setPropertyValue("attachmentsFolder", attachmentsFolder)
